settings.custom_css:
.announcement-link a {
color: #94D976;
border-bottom: 1px solid #94D976;
}
.announcement-bar__text p {
font-size: 14px;
}

.footer__heading {
font-weight: bold;
}

ul.social-icons.is-flex.is-flex-wrap {
    display: inline-flex;
    vertical-align: middle;
    margin: 0 10px;
}

.block__description {
background-color: #f3f3f3;
padding-bottom: 60px;
}
.thumbnail__caption  .product-thumbnail {
    max-width: 250px;
    margin: 0 auto;
}
.bold-ro__product {
margin-top: 25px;
}
.bold-ro__bold-active, .bold-ro__recurring-text {
font-size: 14px;
}
.space {
  margin-top: 50px;
}
.bold-ro__product {
border: none;
}
.sku {
    opacity: .5;
    font-size: 14px;
}
table {
font-size: 16px;
}
.description-custom {
display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-wrap: wrap;
    -moz-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
}

section.resources ul {
margin: 25px;
}

section.resources ul li {
list-style: circle;
}
